mapper.update:
UPDATE bl_topic
id= #{id}, topic_name= #{topicName}, create_user= #{createUser}, create_date= #{createDate}, update_user= #{updateUser}, update_date= #{updateDate},
WHERE id = #{id}

mapper.select:

SELECT 

FROM bl_topic

    id= #{id}
    topic_name= #{topicName}
    create_user= #{createUser}
    create_date= #{createDate}
    update_user= #{updateUser}
    update_date= #{updateDate}
